import { Component } from 'react';
import './search.css';


export default class Search extends Component {
  state = {
    query: ''
  }





  onQuery = (e) => {
    this.setState({
      query: e.target.value
    })
    console.log(this.state.query);
  }


  render() {
    const {items, onAllDone} = this.props

    // console.log(items.filter(user => user.text.toLowerCase().includes(this.state.query)));
    items.map(({ text }) => {
      return (
        text={text} 
        );
      });



      return (
        <div className='search'>
        <input type="text"  placeholder="Type text for search..." onChange={this.onQuery}/>
        <button className='search-btn-all'>All</button>
        <button className='search-btn-done' onClick={onAllDone}>Done</button>
        <button className='search-btn-isImportant'>isImportant</button>
      </div>
    );
  }
}
