import './todo-list-item.css';
import { FaTrash, FaInfo, FaCheck, FaPenToSquare } from 'react-icons/fa6'
import { Component } from 'react';

export default class TodoListItem extends Component {

  state = {
    isDone: false,
    isImportant: this.props.important,
    isRemove: false,
    title: this.props.text

  }

  onDone = () => {
    this.setState({
      isDone: !this.state.isDone
    })
  }

  onImportant = () => {
    this.setState({
      isImportant: !this.state.isImportant
    })
  }

  onDelete = () => {
    this.props.deleteItem(this.props.id)
  }

  onHandleChange = (val) => {
    this.setState(({title}) => {
      title = val
    })
  }



  render() {
    const { text, onEdit, isEdit} = this.props
    const { isDone, isImportant } = this.state



    const textStyle = {
      textDecoration: isDone ? 'line-through' : 'none',
      color: isDone ? '#aaa' : (isImportant ? "#d72020" : "black"),
      fontWeight: isDone ? 'normal' : (isImportant ? "bold" : 'normal'),
    }


    return (
      <li className='list-item' >
       {!isEdit ? <span className='item-text' style={textStyle} onClick={this.onDone}>
          {text}</span> : <input value={this.state.title} onChange={(e) => onHandleChange(e.target.value)}/>}

        <span className='item-btns'>
          <button className='item-btn-edit'onClick={() => onEdit(this.props.id)}><FaPenToSquare /></button>
          <button className='item-btn-done' onClick={this.onDone}><FaCheck /></button>
          <button className='item-btn-important' onClick={this.onImportant}><FaInfo /></button>
          <button className='item-btn-remove' onClick={this.onDelete}><FaTrash /></button>
        </span>
      </li>
    );
  }
}
