import { Component } from 'react';
import './search.css';


export default class Search extends Component {
  state = {
    title: ''
  }

  searchOn = (text) => {
    this.setState(({title}) => {
      return {
        title: text
      }
    })
  }


  render() {
    const { handelSearch } = this.props


    return (
      <div className='search'>
        <input type="text" value={() => handelSearch(this.state.title)} placeholder="Type text for search..." onChange={(e) => this.searchOn(e.target.value)} />
        <button className='search-btn-all'>All</button>
        <button className='search-btn-done'>Done</button>
        <button className='search-btn-isImportant'>isImportant</button>
      </div>
    );
  }
}
