import { Component } from 'react';
import ErrorMessage from '../ErrorMessage/ErrorMessage';
import './search.css';


export default class Search extends Component {
  state = {
    query: '',
    isAllDone: false,
    isError: false
  }



  onAllDone = () => {
    this.setState(({ isAllDone }) => {
      return {
        isAllDone: !isAllDone
      }
    })
    const { items, handleAllDone } = this.props
    const filtered = items.filter(user => user.isDone)
    handleAllDone(filtered)
  }

  onQuery = (e) => {
    this.setState({
      query: e.target.value
    })

    const { items, handelSearch } = this.props
    const filtered = items.filter(item => item.text.toLowerCase().includes(this.state.query))

    if (!filtered) {
      this.setState({
        isError: true
      })
    }

    handelSearch(filtered) 
  }


  render() {
    const {isError} = this.state

    return (
      <div className='search'>
        {!isError ? <input type="text" placeholder="Type text for search..." onChange={this.onQuery}/> : <ErrorMessage/> }
        <button className='search-btn-all'>All</button>
        <button className='search-btn-done' onClick={this.onAllDone}>Done</button>
        <button className='search-btn-isImportant'>isImportant</button>
      </div>
    );
  }
}
