import './add-item.css'
import { Component } from 'react';
import ErrorMessage from '../ErrorMessage/ErrorMessage';




export default class AddItem extends Component {

  state = {
    inputValue: ''
  }

  onInputChange = (event) => {
    this.setState({
      inputValue: event.target.value
    })
  }

  onBtnClick = () => {
    this.props.onAddItem(this.state.inputValue);
    this.setState({inputValue: ''})
  }

  render() {
    return (
      <div className='additem'>
        <div>
          <ErrorMessage message='eerror message' type='error'/>
          <ErrorMessage message='eerror message' type='info'/>

        </div>
        <input type="text" value={this.state.inputValue} placeholder="Item text..." onChange={this.onInputChange} />
        <button onClick={this.onBtnClick}>Add item</button>
      </div>
    )
  }
}
