import { Component } from 'react';
import './search.css';


export default class Search extends Component {
  state = {
    title: ''
  }

  searchOn = (text) => {
    this.setState({ title: text });
    // Call the handelSearch prop function with the search text
    this.props.handelSearch(text);
  };

  render() {



    return (
      <div className='search'>
        <input type="text"  placeholder="Type text for search..." onChange={(e) => this.searchOn(e.target.value)} />
        <button className='search-btn-all'>All</button>
        <button className='search-btn-done'>Done</button>
        <button className='search-btn-isImportant'>isImportant</button>
      </div>
    );
  }
}
